
public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<=10 ;i++)
		{
			System.out.print(i);
		}
		
		System.out.println();
		
		int j=1;
		while(j<=10)
		{
			//statements
			System.out.println(j);
			j++;//j->1->2->3----->11
		}
        System.out.println("hi");
        
        
        
        
	}

}
